class EV():
    def __init__(self, position, destination, soc, required_soc, id):
        self.id = id
        self.position = position
        self.destination = destination
        self.soc = soc
        self.required_soc = required_soc
        self.speed = None
        self.charging_status = False
        self.charging_done_time = None
        self.charging_station = None

    def __eq__(self, other):
        if isinstance(other, EV):
            return self.id == other.id
        return False
    
    def check_adjacent(self, u, v,  traffic_speed):
        if((traffic_speed[u][v] == 0) or (u == v)):
            return False
        else:
            return True
    
    def give_distance(self, u, v, map):
        return map.distance_matrix[u][v]
        
    def check_traffic_speed(self, u, v, map):
        return map.traffic_matrix[u][v]
    
    def moving(self, v):
        moving_speed = random.randint(0.7*self.check_traffic_speed(self.position, v, map), self.check_traffic_speed(self.position, v, map))
        self.speed = moving_speed
        if self.check_adjacent(self.position, v):
            staring_time = time.gmtime()
            while True:
                if(self.give_distance(self.position, v) < (time.gmtime-staring_time)*(moving_speed)):
                    self.position = v
                    return True
        else:
            return False
        
    def check_reached_destination(self):
        if(self.position == self.destination):
            return True
        else:
            return False


