class EVCS():
    def __init__(self, num_of_chargers, charging_speed):
        self.chargers = []
        self.charging_speed = charging_speed

        for _ in range(num_of_chargers):
            k = []
            self.chargers.append(k)
    
    def min_queue(self):
        min_q = len(self.chargers[0])
        min_queue_index = 0

        for i in range(len(self.chargers)):
            if(min_q > len(self.chargers[i])):
                min_q = len(self.chargers[i])
                min_queue_index = i
        
        return min_q, min_queue_index

    
    def confirm_reservation(self, ev, evcs_index, t):
        min_queue = len(self.chargers[0])
        min_queue_index = 0

        for i in range(len(self.chargers)):
            if(min_queue > len(self.chargers[i])):
                min_queue = len(self.chargers[i])
                min_queue_index = i
        
        self.chargers[min_queue_index].append(ev)
        #ev.charging_done_time(evcs_index, min_queue_index, t)
        return min_queue_index

    def remove_ev(self, ev):
        #if(ev.soc == ev.required_soc):
        _ = self.chargers[self.find_queue_number(ev)].pop(0)
        
        ev.charging_status = False
    
    def is_EV_front(self, ev):
        if(self.chargers[self.find_queue_number(ev)][0] == ev.id):
            return True
        else:
            return False
    
    def start_charging(self, ev):
        if(self.is_EV_front(ev)):
            ev.charging_status = True
            self.charging(self.charging_speed)
            starting_time = time.gmtime()
            initial_soc = ev.soc
            while ev.soc < ev.required_soc:
                ev.soc = initial_soc + (time.gmtime() - starting_time)*(self.charging_speed)

    def charging_time(self, ev):
        return (ev.required_soc-ev.soc)/self.charging_speed
    
    def find_queue_number(self, ev):
        for i in range(len(self.chargers)):
            qd = self.chargers[i]
            for j in range(len(qd)):
                if(ev.id == qd[j]):
                    return i, j
        queue_length, queue_index = self.min_queue()
        return queue_index, queue_length

    def waiting_time(self, ev):
        queue_index, queue_length = self.find_queue_number(ev)
        wt = 0
        for i in range(queue_length):
            wt += self.charging_time(self.chargers[queue_index][i])
        # queue_duplicate = self.chargers[queue_index]
        # wt = 0
        # while True:
        #     k = queue_duplicate.pop(0)
        #     if(ev == k):
        #         break
        #     else:
        #         wt += self.charging_time(k)
        #print(wt)
        return wt


