class ITS():
    def __init__(self, no_of_evs, speed, EV_list, traffic_speed):
        self.no_of_evs = no_of_evs
        self.speed = speed  
        self.EV_list = EV_list
        self.traffic_speed = traffic_speed

    def count_EV(self, u, v):
        counter = 0
        for EV in self.EV_list:
            if((EV.position == u and EV.target_node == v) or (EV.position == v and EV.target_node == u)):
                counter += 1
        return counter
    
    def average_velocity(self, ev):
        return ev.speed
    


