    def get_time(self,num_of_nodes):
        for i in range(num_of_nodes):
            for j in range(num_of_nodes):
                self.traffic_matrix[i][j] = random.uniform(0.7*self.max_vel_matrix[i][j],self.max_vel_matrix[i][j])
